mapper.update:
update all_methods
set package_name = #{packageName,jdbcType=VARCHAR},
  javabean_name = #{javabeanName,jdbcType=VARCHAR},
  method_name = #{methodName,jdbcType=VARCHAR},
  param_type = #{paramType,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
SELECT a.method_name,a.param_type,b.change_type as changeType, c.method_name as testedOrNot from(SELECT DISTINCT method_name,param_type from all_methods WHERE package_name = #{packageName} and javabean_name = #{simpleClassName})a
LEFT JOIN (SELECT DISTINCT method_name,param_type,change_type from change_code WHERE package_name = #{packageName} and javabean_name = #{simpleClassName})b ON a.method_name = b.method_name and a.param_type = b.param_type
LEFT JOIN (SELECT DISTINCT method_name,param_type from tested_methods WHERE package_name = #{packageName} and javabean_name = #{simpleClassName})c on a.method_name = c.method_name and a.param_type = c.param_type ;
